/**
 * 音频
 * 接口声明: {"name": "system.audio"}
 */
export default class audio {
    /**
     * 开始播放音频
     */
    static play(): void

    /**
     * 暂停播放音频
     */
    static pause(): void

    /**
     * 停止音频播放，可以通过 play 重新播放音频
     */
    static stop(): void
    /**
     * 播放的音频媒体uri
     */
    src: string;

    /**
     * 音频的当前进度，单位秒，对值设置可以调整播放进度
     */
    currentTime: number;

    /**
     * 音频的播放时长，单位秒，未知返回NaN
     */
    duration: number;

    /**
     * 音频是否自动播放，默认false
     */
    autoplay: boolean;

    /**
     * 音频是否循环播放，默认false
     */
    loop: boolean;

    /**
     * 音频的音量，默认当前系统媒体音量，音量变化范围[0.0,1.0]
     */
    volume: number;

    /**
     * 音频是否静音，默认false
     */
    muted: boolean;

    /**
     * 音频是否在通知栏中显示音频播放状态，默认true
     */
    notificationVisible: boolean;

    /**
     * 在调用play方法后或者autoplay为true时的回调事件
     */
    onplay: () => void;

    /**
     * 在调用pause方法后的回调事件
     */
    onpause: () => void;

    /**
     * 在调用 stop 方法后的回调事件
     */
    onstop: () => void;

    /**
     * 第一次获取到音频数据的回调事件
     */
    onloadeddata: () => void;

    /**
     * 播放结束时的回调事件
     */
    onended: () => void;

    /**
     * 播放时长变化时的回调事件
     */
    ondurationchange: () => void;

    /**
     * 播放发生错误时的回调事件
     */
    onerror: () => void;

    /**
     * 播放进度变化时触发，触发频率4HZ
     */
    ontimeupdate: () => void;
}
